mapper.insert:
insert all
	into board(
		seq
		, id
		, name
		, email
		, subject
		, content
		, hit
		, logtime
		, bcode
	)values(
		#{seq}
		, #{id}
		, #{name}
		, #{email}
		, #{subject}
		, #{content}
		, 0
		, sysdate
		, #{bcode}
	)
	into reboard(
		rseq
		, seq
		, ref
		, lev
		, step
		, pseq
		, reply
	)values(
		reboard_rseq.nextval
		, #{seq}
		, #{ref}
		, 0
		, 0
		, 0
		, 0
	)
select * from dual

mapper.select:
select rowboard.rn , rowboard.seq , rowboard.name , rowboard.id , rowboard.email , rowboard.subject , rowboard.content , rowboard.hit , decode(to_char(rowboard.logtime, 'yyyymmdd') , to_char(sysdate, 'yyyymmdd'), to_char(rowboard.logtime, 'hh24:mi:ss') , to_char(rowboard.logtime, 'yy.mm.dd') ) logtime , rowboard.bcode , rowboard.rseq , rowboard.ref , rowboard.lev , rowboard.step , rowboard.pseq , rowboard.reply from ( select rownum rn, orderboard.* from ( select b.seq, b.name, b.id, b.email, b.subject , b.content, b.hit, b.logtime, b.bcode , r.rseq, r.ref, r.lev, r.step, r.pseq, r.reply from board b inner join reboard r on b.seq = r.seq where b.bcode = #{bcode}
and b.subject like '%' || #{word} || '%'
and b.${key} = #{word}
order by r.ref desc, r.step ) orderboard where rownum <= #{endRow} ) rowboard where rowboard.rn > #{startRow}

mapper.update:
update reboard
set
	step = step +1
where
	ref = #{ref}
	and step > #{step}

mapper.insert:
insert all
	into board(
		seq
		, id
		, name
		, email
		, subject
		, content
		, hit
		, logtime
		, bcode
	)values(
		#{seq}
		, #{id}
		, #{name}
		, #{email}
		, #{subject}
		, #{content}
		, 0
		, sysdate
		, #{bcode}
	)
	into reboard(
		rseq
		, seq
		, ref
		, lev
		, step
		, pseq
		, reply
	)values(
		reboard_rseq.nextval
		, #{seq}
		, #{ref}
		, #{lev} + 1
		, #{step} + 1
		, #{pseq}
		, 0
	)
select * from dual

mapper.update:
update board
set
	subject = #{subject}
	, content = #{content}
	, logtime = sysdate
where
	seq = #{seq}
